mapper.select:
SELECT id, name, path, parent_id, icon, order_num, role, component, status
FROM menu
WHERE id = #{id}

mapper.update:
UPDATE menu
SET name = #{name}, path = #{path}, parent_id = #{parentId}, icon = #{icon},
    order_num = #{orderNum}, role = #{role}, component = #{component}, status = #{status}
WHERE id = #{id}
